from Crypto.PublicKey.ElGamal import generate
from Crypto.Random import get_random_bytes as randfunc

key_pair = generate(2048, randfunc)

print(str(key_pair.p))
print(str(key_pair.g))
print(str(key_pair.y))
print(str(key_pair.x))


r = pow(key_pair.g, key_pair.x, key_pair.p)
print(r)


